const modalForm = app.plugins.plugins.modalforms.api;
const result = await modalForm.openForm("New Task");

// Sanitize and trim the category input
let category = result.get("category") || "";
category = category.replace(/[^a-zA-Z0-9\s]/g, "").trim(); // Remove non-alphanumeric characters and trim whitespace

return `- [ ] #task ${result.get("project")} #${category} ${result.get("description")}`;
